import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { 
  insertCustomerSchema, 
  insertHeatPumpSchema, 
  insertInstallerSchema, 
  insertServiceAreaSchema,
  insertContactSchema,
} from "@shared/schema";
import { ZodError } from "zod";
import Stripe from "stripe";

// Initialize Stripe
const stripe = process.env.STRIPE_SECRET_KEY 
  ? new Stripe(process.env.STRIPE_SECRET_KEY, { apiVersion: "2024-06-20" })
  : null;

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Customer routes
  app.post('/api/customers', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const customerData = insertCustomerSchema.parse({
        ...req.body,
        userId,
      });

      const customer = await storage.createCustomer(customerData);

      // Update user role
      await storage.upsertUser({
        id: userId,
        role: 'customer',
        email: req.user.claims.email,
        firstName: req.user.claims.first_name,
        lastName: req.user.claims.last_name,
        profileImageUrl: req.user.claims.profile_image_url,
      });

      res.json(customer);
    } catch (error) {
      if (error instanceof ZodError) {
        res.status(400).json({ message: "Invalid data", errors: error.errors });
      } else {
        console.error("Error creating customer:", error);
        res.status(500).json({ message: "Failed to create customer" });
      }
    }
  });

  app.get('/api/customers/me', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const customer = await storage.getCustomer(userId);
      res.json(customer);
    } catch (error) {
      console.error("Error fetching customer:", error);
      res.status(500).json({ message: "Failed to fetch customer" });
    }
  });

  app.get('/api/customers', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Forbidden" });
      }

      const customers = await storage.getAllCustomers();
      res.json(customers);
    } catch (error) {
      console.error("Error fetching customers:", error);
      res.status(500).json({ message: "Failed to fetch customers" });
    }
  });

  app.post('/api/customers/:id/subscription', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Forbidden" });
      }

      const customerId = parseInt(req.params.id);
      const { active } = req.body;

      const customer = await storage.updateCustomerSubscription(customerId, active);
      res.json(customer);
    } catch (error) {
      console.error("Error updating subscription:", error);
      res.status(500).json({ message: "Failed to update subscription" });
    }
  });

  // Heat pump routes
  app.post('/api/heat-pumps', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const customer = await storage.getCustomer(userId);

      if (!customer) {
        return res.status(404).json({ message: "Customer not found" });
      }

      const heatPumpData = insertHeatPumpSchema.parse({
        ...req.body,
        customerId: customer.id,
      });

      const heatPump = await storage.createHeatPump(heatPumpData);
      res.json(heatPump);
    } catch (error) {
      if (error instanceof ZodError) {
        res.status(400).json({ message: "Invalid data", errors: error.errors });
      } else {
        console.error("Error creating heat pump:", error);
        res.status(500).json({ message: "Failed to create heat pump" });
      }
    }
  });

  app.get('/api/heat-pumps/me', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const customer = await storage.getCustomer(userId);

      if (!customer) {
        return res.status(404).json({ message: "Customer not found" });
      }

      const heatPumps = await storage.getHeatPumpsByCustomer(customer.id);
      res.json(heatPumps);
    } catch (error) {
      console.error("Error fetching heat pumps:", error);
      res.status(500).json({ message: "Failed to fetch heat pumps" });
    }
  });

  // Installer routes
  app.post('/api/installers', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const installerData = insertInstallerSchema.parse({
        ...req.body,
        userId,
      });

      const installer = await storage.createInstaller(installerData);

      // Update user role
      await storage.upsertUser({
        id: userId,
        role: 'installer',
        email: req.user.claims.email,
        firstName: req.user.claims.first_name,
        lastName: req.user.claims.last_name,
        profileImageUrl: req.user.claims.profile_image_url,
      });

      res.json(installer);
    } catch (error) {
      if (error instanceof ZodError) {
        res.status(400).json({ message: "Invalid data", errors: error.errors });
      } else {
        console.error("Error creating installer:", error);
        res.status(500).json({ message: "Failed to create installer" });
      }
    }
  });

  app.get('/api/installers/me', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const installer = await storage.getInstaller(userId);
      res.json(installer);
    } catch (error) {
      console.error("Error fetching installer:", error);
      res.status(500).json({ message: "Failed to fetch installer" });
    }
  });

  app.get('/api/installers', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Forbidden" });
      }

      const installers = await storage.getAllInstallers();
      res.json(installers);
    } catch (error) {
      console.error("Error fetching installers:", error);
      res.status(500).json({ message: "Failed to fetch installers" });
    }
  });

  app.get('/api/installers/pending', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Forbidden" });
      }

      const installers = await storage.getPendingInstallers();
      res.json(installers);
    } catch (error) {
      console.error("Error fetching pending installers:", error);
      res.status(500).json({ message: "Failed to fetch pending installers" });
    }
  });

  app.post('/api/installers/:id/approve', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Forbidden" });
      }

      const installerId = parseInt(req.params.id);
      const { approved } = req.body;

      const installer = await storage.updateInstallerApproval(installerId, approved);
      res.json(installer);
    } catch (error) {
      console.error("Error updating installer approval:", error);
      res.status(500).json({ message: "Failed to update installer approval" });
    }
  });

  app.get('/api/installers/by-municipality/:municipality', isAuthenticated, async (req: any, res) => {
    try {
      const { municipality } = req.params;
      const installers = await storage.getInstallersByMunicipality(municipality);
      res.json(installers);
    } catch (error) {
      console.error("Error fetching installers by municipality:", error);
      res.status(500).json({ message: "Failed to fetch installers" });
    }
  });

  // Service area routes
  app.post('/api/service-areas', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const installer = await storage.getInstaller(userId);

      if (!installer) {
        return res.status(404).json({ message: "Installer not found" });
      }

      const { serviceAreas } = req.body;

      // Delete existing service areas
      await storage.deleteServiceAreasByInstaller(installer.id);

      // Create new service areas
      const createdAreas = [];
      for (const area of serviceAreas) {
        const serviceAreaData = insertServiceAreaSchema.parse({
          ...area,
          installerId: installer.id,
        });
        const serviceArea = await storage.createServiceArea(serviceAreaData);
        createdAreas.push(serviceArea);
      }

      res.json(createdAreas);
    } catch (error) {
      if (error instanceof ZodError) {
        res.status(400).json({ message: "Invalid data", errors: error.errors });
      } else {
        console.error("Error creating service areas:", error);
        res.status(500).json({ message: "Failed to create service areas" });
      }
    }
  });

  app.get('/api/service-areas/me', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const installer = await storage.getInstaller(userId);

      if (!installer) {
        return res.status(404).json({ message: "Installer not found" });
      }

      const serviceAreas = await storage.getServiceAreasByInstaller(installer.id);
      res.json(serviceAreas);
    } catch (error) {
      console.error("Error fetching service areas:", error);
      res.status(500).json({ message: "Failed to fetch service areas" });
    }
  });

  // Contact routes
  app.post('/api/contacts', isAuthenticated, async (req: any, res) => {
    try {
      const contactData = insertContactSchema.parse(req.body);
      const contact = await storage.createContact(contactData);
      res.json(contact);
    } catch (error) {
      if (error instanceof ZodError) {
        res.status(400).json({ message: "Invalid data", errors: error.errors });
      } else {
        console.error("Error creating contact:", error);
        res.status(500).json({ message: "Failed to create contact" });
      }
    }
  });

  app.get('/api/customers/by-municipality/:municipality', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const installer = await storage.getInstaller(userId);

      if (!installer) {
        return res.status(404).json({ message: "Installer not found" });
      }

      const { municipality } = req.params;
      const customers = await storage.getCustomersByMunicipality(municipality);
      res.json(customers);
    } catch (error) {
      console.error("Error fetching customers by municipality:", error);
      res.status(500).json({ message: "Failed to fetch customers" });
    }
  });

  // Admin stats
  app.get('/api/admin/stats', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Forbidden" });
      }

      const stats = await storage.getAdminStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching admin stats:", error);
      res.status(500).json({ message: "Failed to fetch admin stats" });
    }
  });

  // Stripe subscription routes
  app.post('/api/create-subscription', isAuthenticated, async (req: any, res) => {
    if (!stripe) {
      return res.status(500).json({ message: "Stripe not configured" });
    }

    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      if (user.stripeSubscriptionId) {
        const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId);
        return res.json({
          subscriptionId: subscription.id,
          clientSecret: subscription.latest_invoice?.payment_intent?.client_secret,
        });
      }

      if (!user.email) {
        return res.status(400).json({ message: "User email required" });
      }

      const customer = await stripe.customers.create({
        email: user.email,
        name: `${user.firstName || ""} ${user.lastName || ""}`.trim(),
      });

      if (!process.env.STRIPE_PRICE_ID) {
        return res.status(500).json({ message: "Stripe price ID not configured" });
      }

      const subscription = await stripe.subscriptions.create({
        customer: customer.id,
        items: [{ price: process.env.STRIPE_PRICE_ID }],
        payment_behavior: "default_incomplete",
        payment_settings: { save_default_payment_method: "on_subscription" },
        expand: ["latest_invoice.payment_intent"],
      });

      await storage.updateUserStripeInfo(userId, customer.id, subscription.id);

      res.json({
        subscriptionId: subscription.id,
        clientSecret: subscription.latest_invoice?.payment_intent?.client_secret,
      });
    } catch (error: any) {
      console.error("Error creating subscription:", error);
      res.status(500).json({ message: "Failed to create subscription" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  customers,
  heatPumps,
  installers,
  serviceAreas,
  customerInstallerContacts,
  type User,
  type UpsertUser,
  type Customer,
  type InsertCustomer,
  type HeatPump,
  type InsertHeatPump,
  type Installer,
  type InsertInstaller,
  type ServiceArea,
  type InsertServiceArea,
  type CustomerInstallerContact,
  type InsertContact,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, like, inArray } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<User>;

  // Customer operations
  createCustomer(customer: InsertCustomer): Promise<Customer>;
  getCustomer(userId: string): Promise<Customer | undefined>;
  getCustomerById(id: number): Promise<Customer | undefined>;
  updateCustomerSubscription(customerId: number, active: boolean): Promise<Customer>;
  getCustomersByMunicipality(municipality: string): Promise<Customer[]>;
  getAllCustomers(): Promise<Customer[]>;

  // Heat pump operations
  createHeatPump(heatPump: InsertHeatPump): Promise<HeatPump>;
  getHeatPumpsByCustomer(customerId: number): Promise<HeatPump[]>;
  updateHeatPump(id: number, updates: Partial<HeatPump>): Promise<HeatPump>;

  // Installer operations
  createInstaller(installer: InsertInstaller): Promise<Installer>;
  getInstaller(userId: string): Promise<Installer | undefined>;
  getInstallerById(id: number): Promise<Installer | undefined>;
  updateInstallerApproval(installerId: number, approved: boolean): Promise<Installer>;
  getInstallersByMunicipality(municipality: string): Promise<Installer[]>;
  getAllInstallers(): Promise<Installer[]>;
  getPendingInstallers(): Promise<Installer[]>;

  // Service area operations
  createServiceArea(serviceArea: InsertServiceArea): Promise<ServiceArea>;
  getServiceAreasByInstaller(installerId: number): Promise<ServiceArea[]>;
  deleteServiceAreasByInstaller(installerId: number): Promise<void>;

  // Contact operations
  createContact(contact: InsertContact): Promise<CustomerInstallerContact>;
  getContactsByCustomer(customerId: number): Promise<CustomerInstallerContact[]>;
  getContactsByInstaller(installerId: number): Promise<CustomerInstallerContact[]>;

  // Statistics
  getAdminStats(): Promise<{
    totalCustomers: number;
    activeInstallers: number;
    pendingApprovals: number;
    monthlyRevenue: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserStripeInfo(userId: string, stripeCustomerId: string, stripeSubscriptionId: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        stripeCustomerId,
        stripeSubscriptionId,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  // Customer operations
  async createCustomer(customer: InsertCustomer): Promise<Customer> {
    const [newCustomer] = await db
      .insert(customers)
      .values(customer)
      .returning();
    return newCustomer;
  }

  async getCustomer(userId: string): Promise<Customer | undefined> {
    const [customer] = await db
      .select()
      .from(customers)
      .where(eq(customers.userId, userId));
    return customer;
  }

  async getCustomerById(id: number): Promise<Customer | undefined> {
    const [customer] = await db
      .select()
      .from(customers)
      .where(eq(customers.id, id));
    return customer;
  }

  async updateCustomerSubscription(customerId: number, active: boolean): Promise<Customer> {
    const [customer] = await db
      .update(customers)
      .set({
        subscriptionActive: active,
        updatedAt: new Date(),
      })
      .where(eq(customers.id, customerId))
      .returning();
    return customer;
  }

  async getCustomersByMunicipality(municipality: string): Promise<Customer[]> {
    return await db
      .select()
      .from(customers)
      .where(eq(customers.municipality, municipality))
      .orderBy(desc(customers.createdAt));
  }

  async getAllCustomers(): Promise<Customer[]> {
    return await db
      .select()
      .from(customers)
      .orderBy(desc(customers.createdAt));
  }

  // Heat pump operations
  async createHeatPump(heatPump: InsertHeatPump): Promise<HeatPump> {
    const [newHeatPump] = await db
      .insert(heatPumps)
      .values(heatPump)
      .returning();
    return newHeatPump;
  }

  async getHeatPumpsByCustomer(customerId: number): Promise<HeatPump[]> {
    return await db
      .select()
      .from(heatPumps)
      .where(eq(heatPumps.customerId, customerId))
      .orderBy(desc(heatPumps.createdAt));
  }

  async updateHeatPump(id: number, updates: Partial<HeatPump>): Promise<HeatPump> {
    const [heatPump] = await db
      .update(heatPumps)
      .set({
        ...updates,
        updatedAt: new Date(),
      })
      .where(eq(heatPumps.id, id))
      .returning();
    return heatPump;
  }

  // Installer operations
  async createInstaller(installer: InsertInstaller): Promise<Installer> {
    const [newInstaller] = await db
      .insert(installers)
      .values(installer)
      .returning();
    return newInstaller;
  }

  async getInstaller(userId: string): Promise<Installer | undefined> {
    const [installer] = await db
      .select()
      .from(installers)
      .where(eq(installers.userId, userId));
    return installer;
  }

  async getInstallerById(id: number): Promise<Installer | undefined> {
    const [installer] = await db
      .select()
      .from(installers)
      .where(eq(installers.id, id));
    return installer;
  }

  async updateInstallerApproval(installerId: number, approved: boolean): Promise<Installer> {
    const [installer] = await db
      .update(installers)
      .set({
        approved,
        updatedAt: new Date(),
      })
      .where(eq(installers.id, installerId))
      .returning();
    return installer;
  }

  async getInstallersByMunicipality(municipality: string): Promise<Installer[]> {
    return await db
      .select({
        id: installers.id,
        userId: installers.userId,
        companyName: installers.companyName,
        orgNumber: installers.orgNumber,
        contactPerson: installers.contactPerson,
        email: installers.email,
        phone: installers.phone,
        address: installers.address,
        certified: installers.certified,
        approved: installers.approved,
        rating: installers.rating,
        totalServices: installers.totalServices,
        createdAt: installers.createdAt,
        updatedAt: installers.updatedAt,
      })
      .from(installers)
      .innerJoin(serviceAreas, eq(installers.id, serviceAreas.installerId))
      .where(
        and(
          eq(serviceAreas.municipality, municipality),
          eq(installers.approved, true)
        )
      )
      .orderBy(desc(installers.rating));
  }

  async getAllInstallers(): Promise<Installer[]> {
    return await db
      .select()
      .from(installers)
      .orderBy(desc(installers.createdAt));
  }

  async getPendingInstallers(): Promise<Installer[]> {
    return await db
      .select()
      .from(installers)
      .where(eq(installers.approved, false))
      .orderBy(asc(installers.createdAt));
  }

  // Service area operations
  async createServiceArea(serviceArea: InsertServiceArea): Promise<ServiceArea> {
    const [newServiceArea] = await db
      .insert(serviceAreas)
      .values(serviceArea)
      .returning();
    return newServiceArea;
  }

  async getServiceAreasByInstaller(installerId: number): Promise<ServiceArea[]> {
    return await db
      .select()
      .from(serviceAreas)
      .where(eq(serviceAreas.installerId, installerId))
      .orderBy(asc(serviceAreas.county), asc(serviceAreas.municipality));
  }

  async deleteServiceAreasByInstaller(installerId: number): Promise<void> {
    await db.delete(serviceAreas).where(eq(serviceAreas.installerId, installerId));
  }

  // Contact operations
  async createContact(contact: InsertContact): Promise<CustomerInstallerContact> {
    const [newContact] = await db
      .insert(customerInstallerContacts)
      .values(contact)
      .returning();
    return newContact;
  }

  async getContactsByCustomer(customerId: number): Promise<CustomerInstallerContact[]> {
    return await db
      .select()
      .from(customerInstallerContacts)
      .where(eq(customerInstallerContacts.customerId, customerId))
      .orderBy(desc(customerInstallerContacts.contactedAt));
  }

  async getContactsByInstaller(installerId: number): Promise<CustomerInstallerContact[]> {
    return await db
      .select()
      .from(customerInstallerContacts)
      .where(eq(customerInstallerContacts.installerId, installerId))
      .orderBy(desc(customerInstallerContacts.contactedAt));
  }

  // Statistics
  async getAdminStats(): Promise<{
    totalCustomers: number;
    activeInstallers: number;
    pendingApprovals: number;
    monthlyRevenue: number;
  }> {
    const [customerCount] = await db
      .select({ count: customers.id })
      .from(customers);

    const [activeInstallerCount] = await db
      .select({ count: installers.id })
      .from(installers)
      .where(eq(installers.approved, true));

    const [pendingCount] = await db
      .select({ count: installers.id })
      .from(installers)
      .where(eq(installers.approved, false));

    const [subscriptionCount] = await db
      .select({ count: customers.id })
      .from(customers)
      .where(eq(customers.subscriptionActive, true));

    return {
      totalCustomers: customerCount?.count || 0,
      activeInstallers: activeInstallerCount?.count || 0,
      pendingApprovals: pendingCount?.count || 0,
      monthlyRevenue: (subscriptionCount?.count || 0) * 29,
    };
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import {
  pgTable,
  text,
  varchar,
  timestamp,
  jsonb,
  index,
  serial,
  boolean,
  integer,
  date,
  decimal,
} from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table for Replit Auth
export const users = pgTable("users", {
  id: varchar("id").primaryKey().notNull(),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  role: varchar("role").notNull().default("customer"), // customer, installer, admin
  stripeCustomerId: varchar("stripe_customer_id"),
  stripeSubscriptionId: varchar("stripe_subscription_id"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Customer-specific information
export const customers = pgTable("customers", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").references(() => users.id).notNull(),
  fullName: varchar("full_name").notNull(),
  email: varchar("email").notNull(),
  phone: varchar("phone").notNull(),
  address: text("address").notNull(),
  county: varchar("county").notNull(),
  municipality: varchar("municipality").notNull(),
  subscriptionActive: boolean("subscription_active").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Heat pump information
export const heatPumps = pgTable("heat_pumps", {
  id: serial("id").primaryKey(),
  customerId: integer("customer_id").references(() => customers.id).notNull(),
  model: varchar("model").notNull(),
  brand: varchar("brand").notNull(),
  lastServiceDate: date("last_service_date"),
  nextServiceDue: date("next_service_due"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Installer/company information
export const installers = pgTable("installers", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").references(() => users.id).notNull(),
  companyName: varchar("company_name").notNull(),
  orgNumber: varchar("org_number").notNull(),
  contactPerson: varchar("contact_person").notNull(),
  email: varchar("email").notNull(),
  phone: varchar("phone").notNull(),
  address: text("address"),
  certified: boolean("certified").default(false),
  approved: boolean("approved").default(false),
  rating: decimal("rating", { precision: 3, scale: 2 }).default("0.00"),
  totalServices: integer("total_services").default(0),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Service areas for installers
export const serviceAreas = pgTable("service_areas", {
  id: serial("id").primaryKey(),
  installerId: integer("installer_id").references(() => installers.id).notNull(),
  county: varchar("county").notNull(),
  municipality: varchar("municipality").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Customer-installer interactions
export const customerInstallerContacts = pgTable("customer_installer_contacts", {
  id: serial("id").primaryKey(),
  customerId: integer("customer_id").references(() => customers.id).notNull(),
  installerId: integer("installer_id").references(() => installers.id).notNull(),
  contactedAt: timestamp("contacted_at").defaultNow(),
  status: varchar("status").notNull().default("pending"), // pending, accepted, completed
  notes: text("notes"),
});

// Relations
export const usersRelations = relations(users, ({ one }) => ({
  customer: one(customers, {
    fields: [users.id],
    references: [customers.userId],
  }),
  installer: one(installers, {
    fields: [users.id],
    references: [installers.userId],
  }),
}));

export const customersRelations = relations(customers, ({ one, many }) => ({
  user: one(users, {
    fields: [customers.userId],
    references: [users.id],
  }),
  heatPumps: many(heatPumps),
  contacts: many(customerInstallerContacts),
}));

export const heatPumpsRelations = relations(heatPumps, ({ one }) => ({
  customer: one(customers, {
    fields: [heatPumps.customerId],
    references: [customers.id],
  }),
}));

export const installersRelations = relations(installers, ({ one, many }) => ({
  user: one(users, {
    fields: [installers.userId],
    references: [users.id],
  }),
  serviceAreas: many(serviceAreas),
  contacts: many(customerInstallerContacts),
}));

export const serviceAreasRelations = relations(serviceAreas, ({ one }) => ({
  installer: one(installers, {
    fields: [serviceAreas.installerId],
    references: [installers.id],
  }),
}));

export const customerInstallerContactsRelations = relations(
  customerInstallerContacts,
  ({ one }) => ({
    customer: one(customers, {
      fields: [customerInstallerContacts.customerId],
      references: [customers.id],
    }),
    installer: one(installers, {
      fields: [customerInstallerContacts.installerId],
      references: [installers.id],
    }),
  }),
);

// Zod schemas
export const insertCustomerSchema = createInsertSchema(customers).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertHeatPumpSchema = createInsertSchema(heatPumps).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertInstallerSchema = createInsertSchema(installers).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertServiceAreaSchema = createInsertSchema(serviceAreas).omit({
  id: true,
  createdAt: true,
});

export const insertContactSchema = createInsertSchema(customerInstallerContacts).omit({
  id: true,
  contactedAt: true,
});

// Types
export type UpsertUser = typeof users.$inferInsert;
export type User = typeof users.$inferSelect;
export type Customer = typeof customers.$inferSelect;
export type InsertCustomer = z.infer<typeof insertCustomerSchema>;
export type HeatPump = typeof heatPumps.$inferSelect;
export type InsertHeatPump = z.infer<typeof insertHeatPumpSchema>;
export type Installer = typeof installers.$inferSelect;
export type InsertInstaller = z.infer<typeof insertInstallerSchema>;
export type ServiceArea = typeof serviceAreas.$inferSelect;
export type InsertServiceArea = z.infer<typeof insertServiceAreaSchema>;
export type CustomerInstallerContact = typeof customerInstallerContacts.$inferSelect;
export type InsertContact = z.infer<typeof insertContactSchema>;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { useAuth } from "@/hooks/useAuth";
import NotFound from "@/pages/not-found";
import Landing from "@/pages/landing";
import Home from "@/pages/home";
import Customer from "@/pages/customer";
import Installer from "@/pages/installer";
import Admin from "@/pages/admin";
import Subscribe from "@/pages/subscribe";

function Router() {
  const { isAuthenticated, isLoading } = useAuth();

  return (
    <Switch>
      {isLoading || !isAuthenticated ? (
        <>
          <Route path="/" component={Landing} />
          <Route path="/customer" component={Landing} />
          <Route path="/installer" component={Landing} />
          <Route path="/admin" component={Landing} />
          <Route path="/subscribe" component={Landing} />
        </>
      ) : (
        <>
          <Route path="/" component={Home} />
          <Route path="/customer" component={Customer} />
          <Route path="/installer" component={Installer} />
          <Route path="/admin" component={Admin} />
          <Route path="/subscribe" component={Subscribe} />
        </>
      )}
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

