import { useAuth } from "@/hooks/useAuth";
import { useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import Navigation from "@/components/ui/navigation";
import StatsCard from "@/components/ui/stats-card";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  Users, 
  Settings, 
  TrendingUp, 
  Clock, 
  UserCheck, 
  Check, 
  X,
  Download,
  Mail,
  CreditCard,
  MapPin,
  History,
  UserPlus,
  CheckCircle,
  Eye,
  Edit
} from "lucide-react";

interface AdminStats {
  totalCustomers: number;
  activeInstallers: number;
  pendingApprovals: number;
  monthlyRevenue: number;
}

interface Customer {
  id: number;
  fullName: string;
  email: string;
  municipality: string;
  subscriptionActive: boolean;
  createdAt: string;
}

interface Installer {
  id: number;
  companyName: string;
  orgNumber: string;
  email: string;
  approved: boolean;
  createdAt: string;
}

export default function Admin() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Redirect to login if not authenticated or not admin
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Ikke autorisert",
        description: "Du mÃ¥ logge inn for Ã¥ se denne siden.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }

    if (!isLoading && isAuthenticated && user?.role !== 'admin') {
      toast({
        title: "Ingen tilgang",
        description: "Du har ikke tilgang til administrasjonspanelet.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, user, toast]);

  // Fetch admin stats
  const { data: stats, isLoading: statsLoading } = useQuery({
    queryKey: ["/api/admin/stats"],
    enabled: isAuthenticated && user?.role === 'admin',
    retry: false,
  });

  // Fetch all customers
  const { data: customers, isLoading: customersLoading } = useQuery({
    queryKey: ["/api/customers"],
    enabled: isAuthenticated && user?.role === 'admin',
    retry: false,
  });

  // Fetch pending installers
  const { data: pendingInstallers, isLoading: pendingLoading } = useQuery({
    queryKey: ["/api/installers/pending"],
    enabled: isAuthenticated && user?.role === 'admin',
    retry: false,
  });

  // Approve installer mutation
  const approveInstallerMutation = useMutation({
    mutationFn: async ({ installerId, approved }: { installerId: number; approved: boolean }) => {
      await apiRequest("POST", `/api/installers/${installerId}/approve`, { approved });
    },
    onSuccess: () => {
      toast({
        title: "Vellykket",
        description: "InstallatÃ¸r status oppdatert!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/installers/pending"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/stats"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Ikke autorisert",
          description: "Du er logget ut. Logger inn igjen...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Feil",
        description: "Kunne ikke oppdatere installatÃ¸r status.",
        variant: "destructive",
      });
    },
  });

  // Update subscription mutation
  const updateSubscriptionMutation = useMutation({
    mutationFn: async ({ customerId, active }: { customerId: number; active: boolean }) => {
      await apiRequest("POST", `/api/customers/${customerId}/subscription`, { active });
    },
    onSuccess: () => {
      toast({
        title: "Vellykket",
        description: "Abonnement status oppdatert!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/stats"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Ikke autorisert",
          description: "Du er logget ut. Logger inn igjen...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Feil",
        description: "Kunne ikke oppdatere abonnement status.",
        variant: "destructive",
      });
    },
  });

  if (isLoading || statsLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Navigation />
        <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto"></div>
            <p className="mt-4 text-gray-600">Laster...</p>
          </div>
        </main>
      </div>
    );
  }

  if (!isAuthenticated || user?.role !== 'admin') {
    return null;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Navigation />
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-gray-900 mb-2">Adminpanel</h2>
          <p className="text-muted-foreground">Administrer alle aspekter av VarmepumpeTilsynet</p>
        </div>

        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6 mb-8">
          <StatsCard
            title="Totale kunder"
            value={stats?.totalCustomers || 0}
            icon={Users}
            trend="+12% denne mÃ¥neden"
            trendPositive={true}
          />
          <StatsCard
            title="Aktive installatÃ¸rer"
            value={stats?.activeInstallers || 0}
            icon={Settings}
            trend="+5% denne mÃ¥neden"
            trendPositive={true}
          />
          <StatsCard
            title="MÃ¥nedlig inntekt (kr)"
            value={stats?.monthlyRevenue || 0}
            icon={TrendingUp}
            trend="+18% denne mÃ¥neden"
            trendPositive={true}
          />
          <StatsCard
            title="Ventende godkjenninger"
            value={stats?.pendingApprovals || 0}
            icon={Clock}
            subtitle="Krever handling"
            className="border-orange-200 bg-orange-50"
          />
        </div>

        <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
          {/* Customer Management */}
          <div className="xl:col-span-2 space-y-6">
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="flex items-center">
                    <Users className="text-primary mr-3" />
                    Kundeadministrasjon
                  </CardTitle>
                  <div className="flex space-x-2">
                    <Input
                      placeholder="SÃ¸k kunder..."
                      className="w-64"
                    />
                    <Button size="sm">
                      <Eye className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                {customersLoading ? (
                  <div className="text-center py-8">
                    <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto"></div>
                    <p className="mt-4 text-gray-600">Laster kunder...</p>
                  </div>
                ) : customers && customers.length > 0 ? (
                  <div className="overflow-x-auto">
                    <table className="w-full text-sm">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-4 py-3 text-left font-medium text-gray-700">Kunde</th>
                          <th className="px-4 py-3 text-left font-medium text-gray-700">Kommune</th>
                          <th className="px-4 py-3 text-left font-medium text-gray-700">Abonnement</th>
                          <th className="px-4 py-3 text-left font-medium text-gray-700">Registrert</th>
                          <th className="px-4 py-3 text-left font-medium text-gray-700">Handlinger</th>
                        </tr>
                      </thead>
                      <tbody className="divide-y divide-gray-200">
                        {customers.slice(0, 10).map((customer: Customer) => (
                          <tr key={customer.id}>
                            <td className="px-4 py-3">
                              <div className="font-medium text-gray-900">{customer.fullName}</div>
                              <div className="text-xs text-muted-foreground">{customer.email}</div>
                            </td>
                            <td className="px-4 py-3 text-gray-700">{customer.municipality}</td>
                            <td className="px-4 py-3">
                              <Badge variant={customer.subscriptionActive ? "default" : "destructive"}>
                                {customer.subscriptionActive ? "Aktiv" : "Inaktiv"}
                              </Badge>
                            </td>
                            <td className="px-4 py-3 text-gray-700">
                              {new Date(customer.createdAt).toLocaleDateString('nb-NO')}
                            </td>
                            <td className="px-4 py-3">
                              <div className="flex space-x-2">
                                <Button
                                  size="sm"
                                  variant="outline"
                                  onClick={() => updateSubscriptionMutation.mutate({
                                    customerId: customer.id,
                                    active: !customer.subscriptionActive
                                  })}
                                  disabled={updateSubscriptionMutation.isPending}
                                >
                                  <Edit className="h-3 w-3" />
                                </Button>
                                <Button size="sm" variant="ghost">
                                  <Eye className="h-3 w-3" />
                                </Button>
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>

                    <div className="mt-4 flex justify-between items-center">
                      <span className="text-sm text-muted-foreground">
                        Viser 1-10 av {customers.length} kunder
                      </span>
                      <div className="flex space-x-2">
                        <Button variant="outline" size="sm">Forrige</Button>
                        <Button size="sm">1</Button>
                        <Button variant="outline" size="sm">2</Button>
                        <Button variant="outline" size="sm">3</Button>
                        <Button variant="outline" size="sm">Neste</Button>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-600">Ingen kunder funnet.</p>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Installer Approvals */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <UserCheck className="text-orange-500 mr-3" />
                  InstallatÃ¸r godkjenninger
                </CardTitle>
              </CardHeader>
              <CardContent>
                {pendingLoading ? (
                  <div className="text-center py-8">
                    <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto"></div>
                    <p className="mt-4 text-gray-600">Laster ventende godkjenninger...</p>
                  </div>
                ) : pendingInstallers && pendingInstallers.length > 0 ? (
                  <div className="space-y-4">
                    {pendingInstallers.map((installer: Installer) => (
                      <div key={installer.id} className="border rounded-lg p-4 hover:bg-gray-50">
                        <div className="flex items-center justify-between">
                          <div>
                            <div className="font-medium text-gray-900">{installer.companyName}</div>
                            <div className="text-sm text-muted-foreground">
                              Org.nr: {installer.orgNumber} â€¢ {installer.email}
                            </div>
                            <div className="text-xs text-muted-foreground mt-1">
                              SÃ¸kt: {new Date(installer.createdAt).toLocaleDateString('nb-NO')}
                            </div>
                          </div>
                          <div className="flex space-x-2">
                            <Button
                              size="sm"
                              className="bg-green-600 hover:bg-green-700"
                              onClick={() => approveInstallerMutation.mutate({
                                installerId: installer.id,
                                approved: true
                              })}
                              disabled={approveInstallerMutation.isPending}
                            >
                              <Check className="h-3 w-3 mr-1" />
                              Godkjenn
                            </Button>
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={() => approveInstallerMutation.mutate({
                                installerId: installer.id,
                                approved: false
                              })}
                              disabled={approveInstallerMutation.isPending}
                            >
                              <X className="h-3 w-3 mr-1" />
                              AvslÃ¥
                            </Button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <UserCheck className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-600">Ingen ventende godkjenninger.</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Quick Admin Actions */}
            <Card>
              <CardHeader>
                <CardTitle>Administrative handlinger</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  <Button variant="outline" className="w-full justify-start">
                    <Download className="mr-3 h-4 w-4" />
                    Eksporter kundedata
                  </Button>
                  <Button variant="outline" className="w-full justify-start">
                    <Mail className="mr-3 h-4 w-4" />
                    Send masseutsendelse
                  </Button>
                  <Button variant="outline" className="w-full justify-start">
                    <CreditCard className="mr-3 h-4 w-4" />
