import { useAuth } from "@/hooks/useAuth";
import { useEffect, useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import Navigation from "@/components/ui/navigation";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  UserPlus, 
  Thermometer, 
  CreditCard, 
  MapPin, 
  Bell, 
  Check, 
  Star,
  Calendar,
  Phone,
  Mail
} from "lucide-react";
import { norwegianCounties } from "@/data/municipalities";
import { Link } from "wouter";

interface Customer {
  id: number;
  fullName: string;
  email: string;
  phone: string;
  address: string;
  county: string;
  municipality: string;
  subscriptionActive: boolean;
}

interface HeatPump {
  id: number;
  model: string;
  brand: string;
  lastServiceDate: string;
  nextServiceDue: string;
}

interface Installer {
  id: number;
  companyName: string;
  rating: string;
  totalServices: number;
  certified: boolean;
}

export default function Customer() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedCounty, setSelectedCounty] = useState<string>("");
  const [selectedMunicipality, setSelectedMunicipality] = useState<string>("");

  // Customer form data
  const [customerData, setCustomerData] = useState({
    fullName: "",
    email: "",
    phone: "",
    address: "",
    county: "",
    municipality: "",
  });

  // Heat pump form data
  const [heatPumpData, setHeatPumpData] = useState({
    brand: "",
    model: "",
    lastServiceDate: "",
  });

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Ikke autorisert",
        description: "Du mÃ¥ logge inn for Ã¥ se denne siden.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  // Fetch customer data
  const { data: customer, isLoading: customerLoading } = useQuery({
    queryKey: ["/api/customers/me"],
    enabled: isAuthenticated,
    retry: false,
  });

  // Fetch heat pumps
  const { data: heatPumps, isLoading: heatPumpsLoading } = useQuery({
    queryKey: ["/api/heat-pumps/me"],
    enabled: isAuthenticated,
    retry: false,
  });

  // Fetch local installers
  const { data: installers, isLoading: installersLoading } = useQuery({
    queryKey: ["/api/installers/by-municipality", customer?.municipality],
    enabled: !!customer?.municipality,
    retry: false,
  });

  // Create customer mutation
  const createCustomerMutation = useMutation({
    mutationFn: async (data: any) => {
      await apiRequest("POST", "/api/customers", data);
    },
    onSuccess: () => {
      toast({
        title: "Vellykket",
        description: "Kundeprofil opprettet!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/customers/me"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Ikke autorisert",
          description: "Du er logget ut. Logger inn igjen...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Feil",
        description: "Kunne ikke opprette kundeprofil.",
        variant: "destructive",
      });
    },
  });

  // Create heat pump mutation
  const createHeatPumpMutation = useMutation({
    mutationFn: async (data: any) => {
      await apiRequest("POST", "/api/heat-pumps", data);
    },
    onSuccess: () => {
      toast({
        title: "Vellykket",
        description: "Varmepumpe informasjon lagret!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/heat-pumps/me"] });
      setHeatPumpData({
        brand: "",
        model: "",
        lastServiceDate: "",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Ikke autorisert",
          description: "Du er logget ut. Logger inn igjen...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Feil",
        description: "Kunne ikke lagre varmepumpe informasjon.",
        variant: "destructive",
      });
    },
  });

  const handleCustomerSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    createCustomerMutation.mutate(customerData);
  };

  const handleHeatPumpSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    createHeatPumpMutation.mutate(heatPumpData);
  };

  const handleCountyChange = (county: string) => {
    setSelectedCounty(county);
    setSelectedMunicipality("");
    setCustomerData({ ...customerData, county, municipality: "" });
  };

  const handleMunicipalityChange = (municipality: string) => {
    setSelectedMunicipality(municipality);
    setCustomerData({ ...customerData, municipality });
  };

  const selectedCountyData = norwegianCounties.find(c => c.name === selectedCounty);

  if (isLoading || customerLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Navigation />
        <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto"></div>
            <p className="mt-4 text-gray-600">Laster...</p>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Navigation />
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-gray-900 mb-2">Kundeportal</h2>
          <p className="text-muted-foreground">Administrer din varmepumpe og abonnement</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {/* Customer Registration Form */}
            {!customer && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <UserPlus className="text-primary mr-3" />
                    Registrer deg som kunde
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <form onSubmit={handleCustomerSubmit} className="space-y-4">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="fullName">Fullt navn</Label>
                        <Input
                          id="fullName"
                          value={customerData.fullName}
                          onChange={(e) => setCustomerData({ ...customerData, fullName: e.target.value })}
                          placeholder="Ola Nordmann"
                          required
                        />
                      </div>
                      <div>
                        <Label htmlFor="email">E-post</Label>
                        <Input
                          id="email"
                          type="email"
                          value={customerData.email}
                          onChange={(e) => setCustomerData({ ...customerData, email: e.target.value })}
                          placeholder="ola@example.com"
                          required
                        />
                      </div>
                    </div>

                    <div>
                      <Label htmlFor="address">Adresse</Label>
                      <Input
                        id="address"
                        value={customerData.address}
                        onChange={(e) => setCustomerData({ ...customerData, address: e.target.value })}
                        placeholder="Storgata 1, 0001 Oslo"
                        required
                      />
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="county">Fylke</Label>
                        <Select value={selectedCounty} onValueChange={handleCountyChange}>
                          <SelectTrigger>
                            <SelectValue placeholder="Velg fylke" />
                          </SelectTrigger>
                          <SelectContent>
                            {norwegianCounties.map((county) => (
                              <SelectItem key={county.code} value={county.name}>
                                {county.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                      <div>
                        <Label htmlFor="municipality">Kommune</Label>
                        <Select 
                          value={selectedMunicipality} 
                          onValueChange={handleMunicipalityChange}
                          disabled={!selectedCounty}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Velg kommune" />
                          </SelectTrigger>
                          <SelectContent>
                            {selectedCountyData?.municipalities.map((municipality) => (
                              <SelectItem key={municipality.code} value={municipality.name}>
                                {municipality.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    </div>

                    <div>
                      <Label htmlFor="phone">Telefonnummer</Label>
                      <Input
                        id="phone"
                        type="tel"
                        value={customerData.phone}
                        onChange={(e) => setCustomerData({ ...customerData, phone: e.target.value })}
                        placeholder="+47 123 45 678"
                        required
                      />
                    </div>

                    <Button 
                      type="submit" 
                      disabled={createCustomerMutation.isPending}
                      className="w-full"
                    >
                      {createCustomerMutation.isPending ? "Registrerer..." : "Registrer meg"}
                    </Button>
                  </form>
                </CardContent>
              </Card>
            )}

            {/* Heat Pump Information */}
            {customer && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <Thermometer className="text-primary mr-3" />
                    Varmepumpe informasjon
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <form onSubmit={handleHeatPumpSubmit} className="space-y-4">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="brand">Merke</Label>
                        <Input
                          id="brand"
                          value={heatPumpData.brand}
                          onChange={(e) => setHeatPumpData({ ...heatPumpData, brand: e.target.value })}
                          placeholder="Mitsubishi Electric"
                          required
                        />
                      </div>
                      <div>
                        <Label htmlFor="model">Modell</Label>
                        <Input
                          id="model"
                          value={heatPumpData.model}
                          onChange={(e) => setHeatPumpData({ ...heatPumpData, model: e.target.value })}
                          placeholder="MSZ-LN25VG"
                          required
                        />
                      </div>
                    </div>

                    <div>
                      <Label htmlFor="lastServiceDate">Siste service dato</Label>
                      <Input
                        id="lastServiceDate"
                        type="date"
                        value={heatPumpData.lastServiceDate}
                        onChange={(e) => setHeatPumpData({ ...heatPumpData, lastServiceDate: e.target.value })}
                        required
                      />
                    </div>

                    <Button 
                      type="submit" 
                      disabled={createHeatPumpMutation.isPending}
                    >
                      {createHeatPumpMutation.isPending ? "Lagrer..." : "Lagre informasjon"}
                    </Button>
                  </form>

                  {/* Existing Heat Pumps */}
                  {heatPumps && heatPumps.length > 0 && (
                    <div className="mt-6">
                      <h4 className="font-semibold mb-3">Dine varmepumper</h4>
                      <div className="space-y-2">
                        {heatPumps.map((pump: HeatPump) => (
                          <div key={pump.id} className="p-3 border rounded-lg">
                            <div className="flex justify-between items-start">
                              <div>
                                <div className="font-medium">{pump.brand} {pump.model}</div>
                                <div className="text-sm text-muted-foreground">
